#!/usr/bin/env python3
# -*- coding: UTF-8 -*-

import datetime


class Solution:
    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        a+b+c=0
        a<=b<=c
        0=a+b+c >= a+2b >= 3a, 故a+2b <= 0, a<=0
        0=a+b+c <= 2b+c <= 3c, 故2b+c >= 0, c>=0
        """
        time1 = datetime.datetime.now()

        # 1.减少元素数量
        count = dict()
        new_list = []
        for i in range(len(nums)):
            if nums[i] not in count:
                new_list.append(nums[i])
                count[nums[i]] = 1
            elif (nums[i] == 0 and count[nums[i]] < 3) or (nums[i] != 0 and count[nums[i]] < 2):
                new_list.append(nums[i])
                count[nums[i]] += 1
        nums = new_list

        print(nums)

        # 2.排序
        self.quickSort(nums, 0, len(nums) - 1)
        time2 = datetime.datetime.now()
        print("sort time {0}".format(time2 - time1))

        # 3.简单校验
        if len(nums) < 3:
            return []

        result = set()

        # 4.建立索引
        cache = dict()
        for i in range(len(nums) - 1, -1, -1):
            if nums[i] > 0 and nums[i] not in cache:
                cache[nums[i]] = i
        print(cache)

        i = 0
        n = len(nums)
        while i < n - 2:
            if nums[i] > 0:
                break
            j = i + 1
            while j < n - 1:
                balance = -(nums[i] + nums[j])
                if balance < 0:
                    break
                if balance > 0:
                    if balance in cache and cache[balance] > j:
                        result.add((nums[i], nums[j], balance))
                else:
                    if nums[j + 1] == 0:
                        result.add((0, 0, 0))

                j += 1
            i += 1

        time3 = datetime.datetime.now()
        print("find time {0}".format(time3 - time2))

        print(len(cache))

        list_result = []
        for item in result:
            list_result.append(list(item))

        time4 = datetime.datetime.now()
        print("convert time {0}".format(time4 - time3))
        return list_result

    def quickSort(self, lst, l, r):
        """
        :type lst: List[int]
        :rtype List[int]
        """
        if l < r:
            key = lst[l]
            i = l
            j = r
            while i < j:
                while lst[j] >= key and i < j:
                    j -= 1
                if i < j:
                    lst[i] = lst[j]

                while lst[i] <= key and i < j:
                    i += 1
                if i < j:
                    lst[j] = lst[i]
            lst[i] = key

            self.quickSort(lst, l, i - 1)
            self.quickSort(lst, i + 1, r)

    def binarySearch(self, lst, l, r, value):
        if l > r:
            return None
        if l == r:
            if lst[l] == value:
                return l
            else:
                return None
        mid = (l + r) // 2
        if lst[mid] == value:
            return mid
        elif lst[mid] > value:
            return self.binarySearch(lst, l, mid - 1, value)
        else:
            return self.binarySearch(lst, mid + 1, r, value)


